import React,{Component} from 'react'
import './App.css';
import stateVideo from './stateVideo'
import Nav from './component/Nav'
import AdminVideo from './component/AdminVideo'
import AdminVideoSelected from './component/AdminVideoSelected'

//firebase
import base from './base'

//react router
import{BrowserRouter as Router,Route} from 'react-router-dom'

class App extends Component{
  constructor(props) {
    super(props)
    this.state ={
      mytube:{},
      search:'',
      videoSelected:'',
      uid:'',
      user:{}
    }
}


  componentDidMount(){
    this.ref=base.syncState(`/`,{
      context:this,
      state: 'mytube'
    })
  }

  componentWillUnmount(){
    base.removeBinding(this.ref);
  }

  handleUid=(uid)=>{
    this.setState({search:value})
  }
  
  handleSearch=(value)=>{
    this.setState({search:value})
  }

  handleUser=(value)=>{
    const id=this.state.user.uid
    this.setState({user:value})
  }
  
  handleVue=(key) => {
    const mytube= {...this.state.mytube}
    mytube.video[key].vue++;
    this.setState({mytube})
  
    const videoSelected=key
    this.setState({videoSelected})
  }

  handleLike = (key) => {
    const mytube= {...this.state.mytube}
    mytube.video[key].like++;
    this.setState({mytube})
  }




  addNewUser=(pseudo,uid)=>{
    const mytube={...this.state.mytube}
    const newUser={
      uid:uid,
      pseudo:pseudo
    }
    mytube.user[`user-${Date.now()}`]=newUser
    this.setState({mytube})

    return newUser;
  }

  getUser=(pseudo,uid)=>{
    const Testuser={...this.state.mytube.user}
    let newUser={}
    Object.keys(Testuser)   //on vérifie que le compte n'a pas déja été créer
        .map(key => {
                  if((Testuser[key].uid===uid)){
                    newUser=Testuser[key];
                  }
             }
        );
    
    if(newUser.uid==null){    //si aucun compte n'a été ctrouver on en créer un
      newUser=this.addNewUser(pseudo,uid)
    }

    const user={...this.state.user}   //mise à jour du sate user local 
    this.setState({user:newUser})
  }

  


  render(){





    return (
      <>
       <Router>
         
          <Nav state={this.state} stateMytube={this.state.mytube.video} handleSearch={this.handleSearch} handleUser={this.handleUser} getUser={this.getUser}>
          </Nav>
        
         <Route path='/' exact render={()=><AdminVideo state={this.state} stateMytube={this.state.mytube.video} handleVue={this.handleVue}/>}/>
{/*         
         <Route path='/guess/:slug' exact render={()=><AdminVideoSelected state={this.state.video.video}/>}/>   */}
     
      </Router> 
      </>
    );
  }
}

export default App;
