import React,{useState} from 'react'

import {firabaseInitApp, firebaseInitApp} from '../base'


export default function AdminUpload() {

    const [url,changeUrl]=useState('')


    const onChange=(e)=>{
        const file= e.target.files[0]
        const storageRef=firebaseInitApp.storage().ref()
        const  fileRef=storageRef.child('video/'+file.name)
        fileRef.put().then(()=>{
            console.log("Upload a file");
            fileRef.getDownloadURL().then(function (url) {
                console.log(url);
                change
            });
        })
    }

    const affichage=()=>{
        const storageRef=firebaseInitApp.storage().ref('video')
        storageRef.listAll().then(function(result){
            result.items.forEach(function(imageRef){
                return displayImage(imageRef);
            });
        }).catch(function(error) {
            console.log(error)
      });
    }

      function displayImage(imageRef){
          imageRef.getDownloadUrl().then(function(url){
               return <img src={url} />
          }).catch(function(error){
                console.log(error)
          })

        }
      
    


    return (
        <div>
            
            <input type="file" onChange={onChange}/>

            {affichage()}
        </div>
    )
}
