import React, { Component } from 'react'

import firebase from 'firebase/app'
import 'firebase/auth'
import base,{ firebaseApp} from '../base'


export default class Login extends Component {
    
    state={
        uid:null
    }
    
    componentDidMount(){
        firebase.auth().onAuthStateChanged(user=>{
            if(user){
                this.handleAuth({user})
                this.props.handleUser(this.state.uid)
            }
        })
    }
    
    handleAuth = async authData=>{
        console.log(authData.user.uid)
        
        this.setState({
            uid: authData.user.uid
        })

       let pseudoUid=this.props.getPseudo(this.state.uid)
        
       if(pseudoUid=='' || pseudoUid==null){
           pseudoUid=prompt("Message à afficher")
       }

       this.props.getUser(pseudoUid,this.state.uid)

    }


    authenticate=()=>{
        const authProvider=new firebase.auth.GoogleAuthProvider()
        firebaseApp
            .auth()
            .signInWithPopup(authProvider)
            .then(this.handleAuth)
    }

    logout = async ()=>{
        console.log('déconexion')
        await firebase.auth().signOut()
        this.setState({
            uid:null
        })
    }

    render() {
        if(this.state.uid===null){
            return (
                <div>
                    <p onClick={this.authenticate}>Connexion</p>
                </div>
            )
        }
        else{
            return (
                <div>
                    <p onClick={this.logout}>Déconnexion</p>
                </div>
            )
        }
    }
}
