import React,{Component} from 'react'
import './App.css';
import stateVideo from './stateVideo'
import Nav from './component/Nav'
import AdminVideo from './component/AdminVideo'
import AdminVideoSelected from './component/AdminVideoSelected'

//firebase
import base from './base'

//react router
import{BrowserRouter as Router,Route} from 'react-router-dom'

class App extends Component{
  constructor(props) {
    super(props)
    this.state ={
      mytube:{},
      search:'',
      videoSelected:'',
      user:''
    }
}


  componentDidMount(){
    this.ref=base.syncState(`/`,{
      context:this,
      state: 'mytube'
    })
  }

  componentWillUnmount(){
    base.removeBinding(this.ref);
  }
  
  handleSearch=(value)=>{
    this.setState({search:value})
  }

  handleUser=(value)=>{
    const id=this.state.user.uid
    this.setState({user:value})
  }
  
  handleVue=(key) => {
    const mytube= {...this.state.mytube}
    mytube.video[key].vue++;
    this.setState({mytube})
  
    const videoSelected=key
    this.setState({videoSelected})
  }

  handleLike = (key) => {
    const mytube= {...this.state.mytube}
    mytube.video[key].like++;
    this.setState({mytube})
  }

  addNewUser=(pseudo,uid)=>{
    
    
    const mytube={...this.state.mytube}
    const newUser={
      uid:uid,
      pseudo:pseudo
    }
    mytube.user[`user-${Date.now()}`]=newUser
    this.setState({mytube})

    user={...this.state.user}
    this.setState({})
  }

  


  render(){
    return (
      <>
       <Router>
        <button onClick= {()=>this.addNewUser('test',55555)}>AAAAAA</button>
        <Nav state={this.state.mytube.video} handleSearch={this.handleSearch} handleUser={this.handleUser}>
        </Nav>
        
         <Route path='/' exact render={()=><AdminVideo search={this.state.search} state={this.state.mytube.video} handleVue={this.handleVue}/>}/>
{/*         
         <Route path='/guess/:slug' exact render={()=><AdminVideoSelected state={this.state.video.video}/>}/>   */}
     
      </Router> 
      </>
    );
  }
}

export default App;
