import React,{useState,useRef,useEffect} from 'react'

import './Nav.css'

export const Nav = ({allTitleVideo}) => {

    const [videoSelect,handleChangeSelect]=useState('')

    const handleSubmit=()=>{
        console.log(videoSelect);
    } 

    const divStart=useRef(null);
    const divCenter=useRef(null);
    const divEnd=useRef(null);

    const searchResponsive=()=>{
        divCenter.current()
    }



    return (
        <header>
            <div className='start' ref={divStart}>
                <i className="far fa-play-circle"></i>
                <h2>MyTube</h2>
            </div>

            <div className="center" ref={divCenter}>
                <form>
                    <input 
                        value={videoSelect} 
                        onChange={event => handleChangeSelect(event.target.value)} onClick={handleSubmit} 
                        type='text' 
                        list="video" 
                        id="search-video" 
                        name="video" 
                        placeholder="Recherche"
                    /> 
                        <datalist id="video">   
                            {allTitleVideo}
                        </datalist>
                    <i onClick={searchResponsive}
                    className="fas fa-search"></i>
                </form>
            </div>


            <div className="end" ref={divEnd}>
              <p>Connexion</p>
            </div>
        </header>
    )
}


export default Nav
