import React from 'react'

import './Nav.css'
import Miniature from './Miniature'

import './AdminVideo.css'

import {Link} from 'react-router-dom'

export const AdminVideo = ({stateMytube,handleVue,search}) => {

    //valeur des tag 1=>nature 2=>musique  3=>dessin

    const trieParNom=(tabItems)=>{
            if(search!=='' && search!==null){
                for(let i=0;i<search.length || 0 ;i++){
                    if( search[i]!==tabItems[i] && search[i]!==tabItems[i].toUpperCase() && search[i]!==tabItems[i].toLowerCase()){
                        return false
                    }
                }
            
        }
        return true
    } 

    

    const addVue=(value)=>{
        handleVue(value)
    }

    const allVideo=Object.keys(stateMytube || {})
                .map(items =>
                    {
                    let test=''
                    const path=`guess/video=${items}&&user=${search}`
                    
                    trieParNom(state[items].nom) 
                    ?  test=
                    
                        <Link style={{textDecoration:'none',color:'#000'}}
                             to={path} >
                            <Miniature 
                                key={path} 
                                id={items}
                                name={state[items].nom}
                                url={state[items].url}
                                tag={state[items].tag}
                                vue={state[items].vue}
                                like={stateMytube[items].like}
                                addVue={addVue}
                            />
                        </Link>
                    : test=''

                    return test
                })
   
    return (
        <div className='cards'>
           {allVideo}
        </div>
    )
}
export default AdminVideo
