import React, { Component } from 'react'

import firebase from 'firebase/app'
import 'firebase/auth'
import base,{ firebaseApp} from '../base'


export default class Nav2 extends Component {
    
    state={
        uid:null
    }
    
    componentDidMount(){
        firebase.auth().onAuthStateChanged(user=>{
            if(user){
                this.handleAuth({user})
            }
        })
    }
    
    handleAuth = async authData=>{
        console.log(authData.user.uid)
        this.setState({
            uid: authData.user.uid
        })
    }


    authenticate=()=>{
        const authProvider=new firebase.auth.GoogleAuthProvider()
        firebaseApp
            .auth()
            .signInWithPopup(authProvider)
            .then(this.handleAuth)
    }

    logout = async ()=>{
        console.log('déconexion')
        await firebase.auth().signOut()
        this.setState({
            uid:null
        })
    }

    render() {
        return (
            <div>
                <header>
            <div className='start' ref={divStart}>
                <i className="far fa-play-circle"></i>
                <h2>MyTube</h2>
            </div>

            <div className="center" ref={divCenter}>
                <form>
                    <input 
                    /> 
                        <datalist id="video">   
                            {allVideo}
                        </datalist>
                    <i className="fas fa-search" ref={btnSearch}></i>
                </form>
            </div>


            <div className="end" ref={divEnd}>
              <p>Connexion</p>
            </div>
        </header>
            </div>
        )
    }
}
