import React,{Component} from 'react'
import './App.css';
import stateVideo from './stateVideo'
import Nav from './component/Nav'
import AdminVideo from './component/AdminVideo'

//firebase
import base from './base'

//react router
import{BowserRouter as Router,Route} from 'react-router-dom'

class App extends Component{

  state = {
    video:{},
    search:'',
    videoSelected:''
  }

  componentDidMount(){
    this.ref=base.syncState(`/`,{
      context:this,
      state: 'video'
    })

  }

  componentWillUnmount(){
    base.removeBinding(this.ref);
  }

  handleSearch=(value)=>{
    this.setState({search:value})
  }

  handleVue=(key)=>{
    const video= {...this.state.video}

    video[key].vue++;
    this.setState({video})

    const videoSelected=key
    console.log(videoSelected);
    this.setState({videoSelected})
    console.log(this.state)
  }

  render(){

    return (
      <>
      <Router>
        <Nav state={this.state} handleSearch={this.handleSearch}></Nav>

        <route></route><AdminVideo state={this.state} handleVue={this.handleVue}/>

      </Router>
      </>
    );
  }
}

export default App;
