import React from 'react'

import './Nav.css'
import Miniature from './Miniature'

import './AdminVideo.css'

import {Link} from 'react-router-dom'

export const AdminVideo = ({state,handleVue}) => {

    //valeur des tag 1=>nature 2=>musique  3=>dessin
    console.log(state)
    const trieParNom=(tabItems)=>{
        const search=state.search
       
        if(search!==''){
         
            for(let i=0;i<search.length;i++){
               
                if( search[i]!==tabItems[i] && search[i]!==tabItems[i].toUpperCase() && search[i]!==tabItems[i].toLowerCase()){
                    return false
                }
            }
           
        }
        return true
    } 

    authenticate=()=>{
        const authProvider=new firebase.auth.GoogleAuthProvider()
        firebaseApp
            .auth()
            .signInWithPopup(authProvider)
            .then(this.handleAuth)
    }
    

    const addVue=(value)=>{
        handleVue(value)
    }

    const allVideo=Object.keys(state.video)
                .map(items =>
                    {
                    let test=''
                    const path=`guess/${items}`
                    
                    trieParNom(state.video[items].nom) 
                    ?  test=
                    
                        <Link style={{textDecoration:'none',color:'#000'}}to={path} >
                            <Miniature 
                            key={path} 
                    
                                id={items}
                                name={state.video[items].nom}
                                url={state.video[items].url}
                                tag={state.video[items].tag}
                                vue={state.video[items].vue}
                                like={state.video[items].like}
                                addVue={addVue}
                            />
                        </Link>
                    : test=''

                    return test
                })
   
    return (
        <div className='cards'>
           {allVideo}
        </div>
    )
}
export default AdminVideo
