import React,{Component} from 'react'
import './App.css';
import stateVideo from './stateVideo'
import Nav from './component/Nav'
import AdminVideo from './component/AdminVideo'
import AdminVideoSelected from './component/AdminVideoSelected'

//firebase
import base from './base'

//react router
import{BrowserRouter as Router,Route} from 'react-router-dom'

class App extends Component{
  
  state = {
    video:{},
    search:'',
    videoSelected:'',
    user:{
      uid:'',
      pseudo:''
    }
  }

  componentDidMount(){
    this.ref=base.syncState(`/`,{
      context:this,
      asArray: true,
      state: 'video'
    })
  }

  componentWillUnmount(){
    base.removeBinding(this.ref);
  }
  
  handleSearch=(value)=>{
    this.setState({search:value})
  }

  handleUser=(value)=>{
    const id=this.state.user.uid

    this.setState({user:value})
  }
  
  handleVue=(key) => {
    const video= {...this.state.video}
    video[key].vue++;
    this.setState({video})

    const videoSelected=key
    this.setState({videoSelected})
  }

  handleLike = (key) => {
    const video= {...this.state.video}
    video[key].like++;
    this.setState({video})
  }


 

  render(){
      console.log(this.state)
    return (
      <>
      
       <Router>
       
        <Nav state={this.state.video[1]} handleSearch={this.handleSearch} handleUser={this.handleUser}>
        </Nav>
        
        {/* <Route path='/' exact render={()=><AdminVideo state={this.state} handleVue={this.handleVue}/>}/>
        
        <Route path='/guess/:slug' exact render={()=><AdminVideoSelected state={this.state}/>}/> */}
     
      </Router> 
      </>
    );
  }
}

export default App;
