import React,{Component} from 'react'
import './App.css';
import stateVideo from './stateVideo'
import Nav from './component/Nav'
import AdminVideo from './component/AdminVideo'

//firebase
import base from './base'

class App extends Component{

  state={
    video:stateVideo,
    VideoBase:'',
    search:''
  }

  componentDidMount(){
    base.syncState(`/${this.state.video}`,{
      context:this,
      state: 'videoBase'
    })
  }

  handleSearch=(value)=>{
    this.setState({search:value})
  }

  render(){

    return (
      <>
        <Nav state={this.state} handleSearch={this.handleSearch}></Nav>
        <AdminVideo state={this.state}/>
      </>
    );
  }
}

export default App;
